import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Skill } from 'src/app/model/skill';
import { SkillService } from 'src/app/service/skill.service';

@Component({
  selector: 'app-new-skill',
  templateUrl: './new-skill.component.html',
  styleUrls: ['./new-skill.component.css']
})
export class NewSkillComponent implements OnInit{
  nombre: string;
  porcentaje: number;


  constructor(private skillS: SkillService, private router: Router){}

  ngOnInit(): void {

  }

  onCreate(): void{
    const skill = new Skill(this.nombre, this.porcentaje);
    this.skillS.save(skill).subscribe(
      data => {
        alert("Skill creada correctamente");
        this.router.navigate(['']);
      }, err =>{
        alert("Fallo al añadir la skill");
        this.router.navigate(['']);
      }
    )
  }

}
<div class="container my-5">
    <div class="row justify-content-center">
        <div class="col-md-6">
            <div class="card p-4 shadow">
                <h2 class="text-center mb-4">Agregar Nueva Skill</h2>
                <form (ngSubmit)="onCreateSkill()" novalidate #f="ngForm">
                    <!-- Campo: Nombre de la Skill -->
                    <div class="form-group mb-3">
                        <label for="nombreSkill" class="form-label">Nombre de la Skill</label>
                        <input type="text" class="form-control" id="nombreSkill" name="nombreSkill" [(ngModel)]="nombreSkill" required>
                    </div>
                    <!-- Campo: Nivel de la Skill -->
                    <div class="form-group mb-3">
                        <label for="nivelSkill" class="form-label">Nivel de la Skill (1-100)</label>
                        <input type="number" class="form-control" id="nivelSkill" name="porcentajeSkill" [(ngModel)]="nivelSkill" min="1" max="100" required>
                    </div>
                    <!-- Botón -->
                    <div class="text-center">
                        <button type="submit" class="btn btn-primary me-2">
                            <i class="fa fa-plus"></i> Agregar Skill
                        </button>
                    </div>
                </form>
            </div>
        </div>
    </div>
</div>
